<script>
export default {
  name: 'Logo',
  mounted() {
    const logo = document.querySelector('.logo');
    const cube = document.querySelector('.cube');

    logo.addEventListener('click', () => {
      cube.classList.add('spin')
    });

    cube.addEventListener('animationend', () => {
      cube.classList.remove('spin')
    });
  }
}
</script>

<style lang="scss" scoped>
$size: 40px;

.logo {
  width: $size;
  height: $size;
  perspective: calc(#{$size} * 4);
	perspective-origin: 50% calc(#{$size} / 2);
}

.cube {
	position: relative;
  width: $size;
  height: $size;
	transform-style: preserve-3d;
}

.cube svg {
	position: absolute;
	width: $size;
	height: $size;
  font-family: sans-serif;
  background-image: url('https://upload.wikimedia.org/wikipedia/commons/2/24/LEGO_logo.svg');
  background-size: cover;
}

.front {
	transform: translateZ(calc(#{$size} / 2));
}

.back {
	transform: translateZ(calc(#{$size} / -2)) rotateY(180deg);
}

.top {
	transform: rotateX(-270deg) translateY(calc(#{$size} / -2));
	transform-origin: top center;
}

.bottom {
  transform: rotateX(-90deg) translateY(calc(#{$size} / 2));
	transform-origin: bottom center;
}

.left {
	transform: rotateY(270deg) translateX(calc(#{$size} / -2));
	transform-origin: center left;
}

.right {
	transform: rotateY(-270deg) translateX(calc(#{$size} / 2));
	transform-origin: top right;
}

@keyframes spin {
  from { 
    transform: rotateY(0);
  }
  to { 
    transform: rotateY(180deg);
  }
}

.spin {
  animation: spin 2s linear;
}
</style>